import React, { Component } from 'react';
import sunny from "../images/sunny.png";
import rainy from "../images/rainy.png";

class WeatherComp extends Component {

    constructor(props) {
        super(props);

        this.day_weather = {
            high_temp: 100,
            low_temp: 0,
            day: "Monday",
        };

        this.state = {
            days: [],
        };
    }

    SetWeather() {
        var day_names = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
        let days = [];
        for (let name in day_names) {
            let temp_day = this.day_weather;
            temp_day.day = day_names[name];
            temp_day.high_temp = 70;
            temp_day.low_temp = 30;
            days.push(temp_day);
        }
        console.log(days);
        this.setState({ days });
    }

    GenerateDays() {
        let days = this.state.days;

        return (
            <div className="row">
                <div className="col-sm">
                    {days[0].name}
                    {days[0].high_temp}
                    {days[0].low_temp}
                </div>
                <div className="col-sm">
                    {days[1].name}
                    {days[1].high_temp}
                    {days[1].low_temp}
                </div>
                <div className="col-sm">
                    {days[2].name}
                    {days[2].high_temp}
                    {days[2].low_temp}
                </div>
                <div className="col-sm">
                    {days[3].name}
                    {days[3].high_temp}
                    {days[3].low_temp}
                </div>
            </div>
        ) 
    }

    render() {

        this.SetWeather();

        return (

            <div className="container">
                {/* <div className="row">
                    <div className="col-sm">
                        {this.state.day}
                        <img src={sunny} alt=""/>
                        <br/>
                        <b>{this.state.high_temp}</b>
                    </div>
                    <div className="col-sm">
                        <img src={rainy} alt=""/>
                    </div>
                </div> */}
                {this.GenerateDays()}
            </div>

        );

    }

}

export default WeatherComp;
